package main

import "strings"

func maximumSubsequenceCount(text string, pattern string) int64 {
	if pattern[0] == pattern[1] {
		cnt := strings.Count(text, pattern[:1])
		return int64((cnt + 1) * cnt / 2)
	}

	cnta, cntb := int64(0), int64(0)
	ans := int64(0)
	for i := range text {
		if text[i] == pattern[0] {
			cnta++
		} else if text[i] == pattern[1] {
			cntb++
			ans += cnta
		}
	}

	ans += max(cnta, cntb)

	return ans
}

func main() {
	println(maximumSubsequenceCount("abdcdbc", "ac"))
	println(maximumSubsequenceCount("aabb", "ab"))
	println(maximumSubsequenceCount("", "rr"))

	//strings.Count("", "r")
	//println((32 * 31) / 2)
}
